// --- Quiz State ---
const NUM_QUIZZES = 13;
const WORDS_PER_QUIZ = Math.ceil(words.length / NUM_QUIZZES);
let quizSets = [];
let currentQuizIndex = -1;
let currentQuestionIndex = 0;
let currentQuizQuestions = [];
let userAnswers = [];
let score = 0;
